import base64
import os

# Using the valid base64 data from 'save_file' for all icons as a temporary fix.
valid_icon_data = "-"

icons = {
    "new_file": valid_icon_data,
    "open_file": valid_icon_data,
    "save_file": valid_icon_data
}

output_dir = r"C:\Users\lab\Documents\BUT1\AutomaTeX\resources\icons"
os.makedirs(output_dir, exist_ok=True)

for name, data in icons.items():
    try:
        image_data = base64.b64decode(data)
        with open(os.path.join(output_dir, f"{name}.png"), "wb") as f:
            f.write(image_data)
        print(f"Successfully created {name}.png")
    except Exception as e:
        print(f"Failed to decode or save {name}.png: {e}")